"use strict";const showNotice=(e=!0)=>{const t=document.getElementById("js-notice-wr"),o=e?document.getElementById("js-notice-success"):document.getElementById("js-notice-err"),n=e?1500:5e3;t.classList.remove("hidden"),o.classList.remove("hidden"),setTimeout((()=>{t.classList.add("hidden"),o.classList.add("hidden")}),n)},handleSubmit=async e=>{e.preventDefault();const t=document.getElementById("js-contact-form"),o=new FormData(e.target),n=getEl("js-form-service").value||"None";o.append("Service type",n);try{const n=await fetch(e.target.action,{method:"POST",body:o,headers:{Accept:"application/json"}});n.ok?(t.reset(),showNotice(!0)):n.json().then((e=>{showNotice(!1),console.error("Server error",e)}))}catch(e){showNotice(!1),console.error("Unknown error",e)}},startContactListener=()=>{document.getElementById("js-contact-form").addEventListener("submit",handleSubmit)};